mapper.select:
SELECT u_id, email, pw, level, nickname, src
FROM user
WHERE email = #{email}

SELECT u_id, email, pw, level, nickname, src
FROM user
WHERE u_id = #{u_id}

SELECT u_id
FROM user
WHERE email = #{email}

mapper.update:
UPDATE user
SET pw = #{pw}, level = #{level},  nickname = #{nickname}, src = #{src}
where email = #{email}
